--
-- Estructura de la taula `wp_56_posts`
--

CREATE TABLE IF NOT EXISTS `wp_56_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

--
-- Bolcant dades de la taula `wp_56_posts`
--

INSERT INTO `wp_56_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-04-17 11:49:17', '2015-04-17 11:49:17', 'Benvingut a <a href="http://agora/blocs/">XTEC Blocs</a>. Aquest és el vostre primer article. Editeu-lo o suprimiu-lo, aleshores comenceu a publicar!', 'Hola, món!', '', 'publish', 'open', 'open', '', 'hola-mon', '', '', '2015-04-17 11:49:17', '2015-04-17 11:49:17', '', 0, 'http://agora/blocs/provanoubloc/?p=1', 0, 'post', '', 1),
(2, 1, '2015-04-17 11:49:17', '2015-04-17 11:49:17', 'Aquesta és una pàgina d''exemple. És diferent d''una article del bloc perquè romandrà sempre al mateix lloc i es mostrarà al menú de navegació de la pàgina web (a molts temes). La majoria de gent comença amb una pàgina quant a que els presenta als visitants potencials de la pàgina web. Podria ser quelcom com això:\n\n<blockquote>Hola a tothom! Treballo de missatger en bicicleta de dia, sóc un aspirant a actor de nit, i aquest és el meu bloc. Visc a Barcelona, tinc un gos meravellós que es diu Pep, i m''agraden les calçotades (i quedar atrapat per la pluja.)</blockquote>\n\n... o potser això:\n<blockquote>La companyia Enginys XYZ es va fundar el 1971, i ha proveït al públic d''enginys de qualitat des de llavors. Ubicada a Gotham City, XYZ dóna treball a més de 2.000 persones i fa tot tipus de coses impressionants per a la comunitat de Gotham.</blockquote>\n\nCom a blocaire nou o nova del WordPress, hauríeu d''anar al <a href="https://agora/blocs/provanoubloc/wp-admin/">tauler</a> per suprimir aquesta pàgina i crear-ne de noves amb el vostre contingut. A passar-ho bé!', 'Pàgina d''exemple', '', 'publish', 'open', 'open', '', 'pagina-exemple', '', '', '2015-04-17 11:49:17', '2015-04-17 11:49:17', '', 0, 'http://agora/blocs/provanoubloc/?page_id=2', 0, 'page', '', 0);

